import React, { Component } from "react";

import { library } from "@fortawesome/fontawesome-svg-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faFacebookSquare,
  faInstagram
} from "@fortawesome/free-brands-svg-icons";
import { faPaperPlane, faTimes } from "@fortawesome/free-solid-svg-icons";

import { connect } from "react-redux";
import { MENU } from "../redux";
import { Link } from "react-router-dom";

library.add(faFacebookSquare);
library.add(faInstagram);
library.add(faPaperPlane);
library.add(faTimes);

class Menu extends Component {
  constructor(props) {
    super(props);

    this.closeMenu = this.closeMenu.bind(this);
  }

  closeMenu() {
    this.props.changeMenuState(false);
  }

  render() {
    return (
      <div className={this.props.menu ? "Menu active-menu" : "Menu"}>
        <div>
          <span className="close" onClick={this.closeMenu}>
            <FontAwesomeIcon size="2x" icon={faTimes} />
          </span>
        </div>
        <div>
          <ul>
            <li className="li-1">Homepage</li>
            <li className="li-2">Biografia</li>
            <li className="li-3">Sinopse</li>
            <li className="li-4">O Espetáculo</li>
            <li className="li-5">Tema</li>
          </ul>
        </div>
        <div className="social">
          <ul>
            <li>
              <FontAwesomeIcon icon={faFacebookSquare} />
            </li>
            <li>
              <FontAwesomeIcon icon={faInstagram} />
            </li>
            <li>
              <FontAwesomeIcon icon={faPaperPlane} />
            </li>
          </ul>
        </div>
      </div>
    );
  }
}

//Obter Redux State
const mapStateToProps = function(state) {
  return {
    menu: state.menu
  };
};

//Mudar Redux State
const mapDispatchToProps = dispatch => {
  return {
    changeMenuState: menu => {
      dispatch(MENU(menu));
    }
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Menu);
